<template>
  <img :src="base" alt="">
</template>

<script>

'use strict'

import { getImg } from '@/jbot/img.js'

export default {
  props: ['url'],

  data () {
    return {
      base: 'data:image/jpeg;base64,'
    }
  },

  watch: {
    url (val) {
      if (val) {
        getImg(val).then(res => {
          if (res) {
            // console.log('watch 成功')
            this.base = res
          } else {
            // console.log('watch 失败')
            this.base = 'data:image/jpeg;base64,'
          }
        })
      }
    }
  },

  mounted () {
    if (this.url) {
      getImg(this.url).then(res => {
        if (res) {
          // console.log('mounted 成功')
          this.base = res
        } else {
          // console.log('mounted 失败')
          this.base = 'data:image/jpeg;base64,'
        }
      })
    }
  }
}
</script>

<style scoped>
img {
  width: 40px;
  height: 40px;
  border-radius: 20px;
  overflow-y: hidden;
}
</style>

